import React, { useState } from 'react';

const Products = () => {
  const [viewType, setViewType] = useState('grid');
  const [captions, setCaptions] = useState([]);

  const toggleView = () => {
    setViewType(prevType => (prevType === 'grid' ? 'list' : 'grid'));
  };

  const handleCaptionChange = (index, event) => {
    const newCaptions = [...captions];
    newCaptions[index] = event.target.value;
    setCaptions(newCaptions);
  };

  const images = [
    { id: 1, src: 'path-to-image-1.jpg' },
    { id: 2, src: 'path-to-image-2.jpg' },
    { id: 3, src: 'path-to-image-3.jpg' },
    // Add more images as needed
  ];

  return (
    <div>
      <button onClick={toggleView}>
        Switch to {viewType === 'grid' ? 'List' : 'Grid'} View
      </button>

      {viewType === 'grid' ? (
        <div className="grid-container">
          {images.map(image => (
            <div key={image.id} className="grid-item">
              <img src={image.src} alt={`Image ${image.id}`} />
              <textarea
                placeholder="Add a caption..."
                value={captions[image.id - 1] || ''}
                onChange={event => handleCaptionChange(image.id - 1, event)}
              />
            </div>
          ))}
        </div>
      ) : (
        <ul className="list-container">
          {images.map(image => (
            <li key={image.id} className="list-item">
              <img src={image.src} alt={`Image ${image.id}`} />
              <textarea
                placeholder="Add a caption..."
                value={captions[image.id - 1] || ''}
                onChange={event => handleCaptionChange(image.id - 1, event)}
              />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Products;
